patch:
diff --git a/core/src/main/java/org/apache/accumulo/core/client/mapreduce/RangeInputSplit.java b/core/src/main/java/org/apache/accumulo/core/client/mapreduce/RangeInputSplit.java
index 05316a1ae..15c6185d3 100644
--- a/core/src/main/java/org/apache/accumulo/core/client/mapreduce/RangeInputSplit.java
+++ b/core/src/main/java/org/apache/accumulo/core/client/mapreduce/RangeInputSplit.java
@@ -144,6 +144,9 @@ public long getLength() throws IOException {
   @Override
   public void readFields(DataInput in) throws IOException {
     range.readFields(in);
+    if (in.readBoolean()) {
+      table = in.readUTF();
+    }
     int numLocs = in.readInt();
     locations = new String[numLocs];
     for (int i = 0; i < numLocs; ++i)
@@ -220,6 +223,12 @@ public void readFields(DataInput in) throws IOException {
   @Override
   public void write(DataOutput out) throws IOException {
     range.write(out);
+
+    out.writeBoolean(null != table);
+    if (null != table) {
+      out.writeUTF(table);
+    }
+
     out.writeInt(locations.length);
     for (int i = 0; i < locations.length; ++i)
       out.writeUTF(locations[i]);
