+          m.putDelete(new Text(update.getColFamily()), new Text(update.getColQualifier()), viz);
+        } else {
+          m.put(new Text(update.getColFamily()), new Text(update.getColQualifier()), viz, new Value(value));
+        }
       }
     }
   }
