@@ -268,11 +267,12 @@ private boolean isRemoved(String name) {

     @Override
     public boolean isModified() {
+        NodeState baseState = getBaseState();
         if (writeState == null) {
             return false;
         }
         else {
-            NodeState baseState = getBaseState();
+            Map<String, MutableNodeState> nodes = writeState.nodes;
             for (Entry<String, MutableNodeState> n : writeState.nodes.entrySet()) {
                 if (n.getValue() == null) {
                     return true;
@@ -308,6 +308,7 @@ public NodeState getNodeState() {

     @Override
     public NodeState getBaseState() {
+        read();
         return baseState;
     }
